import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import axios from 'axios';

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const SalesChart = () => {
    const [chartData, setChartData] = useState({
        labels: [],
        datasets: [
            {
                label: 'Total Sales Over Time',
                data: [],
                backgroundColor: 'rgba(75,192,192,0.2)',
                borderColor: 'rgba(75,192,192,1)',
                borderWidth: 1,
            },
        ],
    });

    const fetchData = async () => {
        try {
            const response = await axios.get('http://localhost:5001/api/sales');
            const { data } = response;

            const labels = data.map(item => item.date); // Assuming data is an array of objects with `date` and `totalSales`
            const salesData = data.map(item => item.totalSales);

            setChartData({
                labels,
                datasets: [
                    {
                        label: 'Total Sales Over Time',
                        data: salesData,
                        backgroundColor: 'rgba(75,192,192,0.2)',
                        borderColor: 'rgba(75,192,192,1)',
                        borderWidth: 1,
                    },
                ],
            });
        } catch (error) {
            console.error("Error fetching data", error);
        }
    };

    useEffect(() => {
        fetchData();
    }, []);

    return (
        <div>
            <h2>Total Sales Over Time</h2>
            {chartData.labels.length > 0 ? (
                <Line data={chartData} />
            ) : (
                <p>Loading data...</p>
            )}
        </div>
    );
};

export default SalesChart;
